mapper.delete:
update blog_comment
set deleted = 1
where id = #{id}
  and publisher = #{publisher}

mapper.select:
select *
from blog_comment
where blog_id = #{blogId}
  and base_comment = #{baseComment}
  and deleted = 0
  and hidden = #{hidden}
limit #{startIndex}, #{rowNum}

mapper.update:
update blog_comment
set hidden = 1
where id = #{id}
  and receiver = #{currentUser}

update blog_comment
set comment_num = comment_num + #{incr}
where id = #{id}
