mapper.delete:
update follow
set deleted = 1
where follower = #{follower}
  and followee = #{followee}
  and type = #{type}

mapper.select:
select count(*)
from follow
where follower = #{follower}
  and followee = #{followee}
  and type = #{type}
  and deleted = 0

select *
from follow
where follower = #{follower}
  and deleted = 0
limit #{startIndex}, #{rowNum}

select *
from follow
where followee = #{followee}
  and type = 1
  and deleted = 0
limit #{startIndex}, #{rowNum}
